const header=document.querySelector(".header"),navlink=document.querySelectorAll(".nav-link"),menuButton=document.querySelector(".menu-button"),logoText=document.querySelector(".logo-text");function myFunction(){document.body.classList.toggle("stop-scrolling"),document.querySelector(".nav-items").classList.toggle("ham-items"),document.querySelector(".menu-button").classList.toggle("bar-one"),document.querySelector(".menu-button").classList.toggle("bar-two"),document.querySelector(".menu-button").classList.toggle("bar-three")}window.onscroll=function(){var e=window.scrollY;if(console.log(e),e>=100){header.classList.add("white"),logoText.classList.add("logoText-scroll");for(var t=0;t<navlink.length;t++)navlink[t].classList.add("navlink-scroll");menuButton.classList.add("menuButton-scroll")}else{header.classList.remove("white"),logoText.classList.remove("logoText-scroll");for(t=0;t<navlink.length;t++)navlink[t].classList.remove("navlink-scroll");menuButton.classList.remove("menuButton-scroll")}};