const header=document.querySelector(".header"),navlink=document.querySelectorAll(".nav-link");function myFunction(){var e=document.querySelector(".nav-items"),l=document.querySelectorAll(".menu-button");"block"===e.style.display?e.style.display="none":(e.style.display="block",e.style.height="100%",e.style.position="fixed",l.style.backgroundColor="red")}window.onscroll=function(){var e=window.scrollY;if(console.log(e),e>=100){header.classList.add("white");for(var l=0;l<navlink.length;l++)navlink[l].classList.add("navlink-scroll")}else{header.classList.remove("white");for(l=0;l<navlink.length;l++)navlink[l].classList.remove("navlink-scroll")}};